// Generated by Claude.
// Compact library card with different styles for owned/shared libraries
// Supports long press for actions on owned libraries
import { useRef, useCallback } from 'react';
import { Users, UserCheck, ChevronRight } from 'lucide-react';
import { cn } from '@/lib/utils';

const LONG_PRESS_DURATION = 500;

const LibraryCard = ({ library, onClick, onLongPress }) => {
  const isSharedFromOther = !!library.sharedFrom;
  const isSharedToOthers = library.sharedTo?.length > 0;
  const pressTimer = useRef(null);
  const isLongPress = useRef(false);

  const handleTouchStart = useCallback(() => {
    isLongPress.current = false;
    pressTimer.current = setTimeout(() => {
      isLongPress.current = true;
      // Only trigger long press for owned libraries
      if (!isSharedFromOther && onLongPress) {
        onLongPress(library);
      }
    }, LONG_PRESS_DURATION);
  }, [library, isSharedFromOther, onLongPress]);

  const handleTouchEnd = useCallback(() => {
    if (pressTimer.current) {
      clearTimeout(pressTimer.current);
      pressTimer.current = null;
    }
  }, []);

  const handleClick = useCallback(() => {
    // Don't trigger click if it was a long press
    if (!isLongPress.current) {
      onClick?.(library);
    }
  }, [library, onClick]);

  return (
    <button
      onClick={handleClick}
      onTouchStart={handleTouchStart}
      onTouchEnd={handleTouchEnd}
      onTouchCancel={handleTouchEnd}
      onContextMenu={(e) => {
        // Prevent context menu on long press, trigger action instead
        if (!isSharedFromOther && onLongPress) {
          e.preventDefault();
          onLongPress(library);
        }
      }}
      className={cn(
        'w-full flex items-center gap-3 p-3 rounded-lg border text-left transition-colors select-none',
        'hover:bg-accent/50 active:bg-accent',
        isSharedFromOther
          ? 'border-blue-200 bg-blue-50/50 dark:border-blue-900 dark:bg-blue-950/30'
          : 'border-border bg-card'
      )}
    >
      {/* Content */}
      <div className="flex-1 min-w-0">
        <div className="flex items-center gap-2">
          <span className="font-medium truncate">{library.name}</span>
          {/* Shared indicators */}
          {isSharedFromOther && (
            <span className="inline-flex items-center gap-1 text-xs text-blue-600 dark:text-blue-400">
              <UserCheck className="h-3 w-3" />
            </span>
          )}
          {isSharedToOthers && (
            <span className="inline-flex items-center gap-1 text-xs text-muted-foreground">
              <Users className="h-3 w-3" />
            </span>
          )}
        </div>

        <div className="flex items-center gap-2 text-sm text-muted-foreground">
          {isSharedFromOther ? (
            <span className="truncate">
              Shared by {library.sharedFrom}
            </span>
          ) : (
            <>
              <span>{library.totalItems} {library.totalItems === 1 ? 'item' : 'items'}</span>
              {isSharedToOthers && (
                <>
                  <span className="text-border">Â·</span>
                  <span className="truncate">
                    Shared with {library.sharedTo.length} {library.sharedTo.length === 1 ? 'user' : 'users'}
                  </span>
                </>
              )}
            </>
          )}
        </div>
      </div>

      {/* Chevron */}
      <ChevronRight className="h-5 w-5 shrink-0 text-muted-foreground" />
    </button>
  );
};

export default LibraryCard;
